using VVI=vector<vector<int>>;


int rank_f2(VVI vec){
	int n=vec.size();
	if(n==0) return 0;
	int m=vec[0].size();

	// n*m
	int sumi=0;
	for(int j=0;j<m;j++){
		int it=-1;
		for(int i=sumi;i<n;i++)if(vec[i][j]){
			bool bo=0;
			it=i;
			break;
		}
		if(it>=0){
			if(it!=sumi)swap(vec[it],vec[sumi]);
			sumi++;
			for(int i=sumi;i<n;i++)if(vec[i][j]){
				for(int h=j;h<m;h++) vec[i][h]^=vec[sumi-1][h];
			}
		}
	}
	return sumi;
}


