ll rank1(vector< vector<ll> >& mat) {
	ll h = mat.size();
	ll w = mat[0].size();
	ll now = 0;
	for (ll i = 0; i < w; i++) {
		ll pivot = -1;
		for (ll j = now; j < h; j++) {
			if (mat[j][i] != 0) {
				pivot = j;
				break;
			}
		}
		if (pivot == -1) {
			continue;
		}
		if (pivot != now) {
			swap(mat[now], mat[pivot]);
		}
		// for (ll j = i + 1; j < w; j++) {
		//     mat[now][j] /= mat[now][i];
		// }
		for (ll j = now + 1; j < h; j++) {
			if (mat[j][i]) {
				for (ll k = i + 1; k < w; k++) {
					mat[j][k] ^= mat[now][k];
				}
			}
		}
		now++;
		if (now == h) {
			break;
		}
	}
	return now;
}

ll mpow(int n, int k, int p) {
	if (k == 0)return 1;
	if (k % 2)return 1LL * n * mpow(n, k - 1, p) % p;
	else {
		ll sub = mpow(n, k / 2, p);
		return 1LL * sub * sub% p;
	}
}

int main() {
	int n, m; cin >> n >> m;
	vector<vector<ll>> a(n);
	rep(i, 0, n) {
		a[i].resize(m);
		rep(j, 0, m) {
			cin >> a[i][j];
		}
	}
	int r = rank1(a);

	ll ans = (mpow(2, n + m - 1, MOD) - mpow(2, n + m - r - 1, MOD) + MOD) % MOD;
	cout << ans << endl;
	return 0;
}

