using Mod2 = ModInt<2>;
using Type = ModInt<998244353>;
using Vector = vector<Mod2>;
using Matrix = vector<Vector>;

int Rank(Matrix &A) {
    const int N = A.size(), M = A[0].size();
    int rank = 0;
    vector<bool> used(N, false);

    for (int c = 0; c < M; ++c) {
        int pivot = 0;
        while (pivot < N && (used[pivot] || A[pivot][c] == 0)) ++pivot;
        if (pivot == N) continue;

        ++rank;
        used[pivot] = true;
        Mod2 v = A[pivot][c];
        for (int j = 0; j < M; ++j) A[pivot][j] /= v;
        for (int r = 0; r < N; ++r) {
            if (r == pivot) continue;
            for (int j = c + 1; j < M; ++j)
                A[r][j] -= A[r][c] * A[pivot][j];
        }
    }

    return rank;
}

int main() {
    cin.tie(0); ios::sync_with_stdio(false);

    int n, m;
    cin >> n >> m;
    Matrix A(n, Vector(m));
    for (int i = 0; i < n; ++i)
        for (int j = 0; j < m; ++j)
            cin >> A[i][j];

    int rank = Rank(A);
    Type res = Type(2).pow(m + n - 1) - Type(2).pow(m + n - rank - 1);
    cout << res << endl;

    return 0;
}

