#ifndef CLASS_MODINT
#define CLASS_MODINT

#include <cstdint>

template<std::uint32_t mod>
class modint {
private:
	std::uint32_t n;
public:
	modint() : n(0) {};
	modint(std::int64_t n_) : n((n_ >= 0 ? n_ : mod - (-n_) % mod) % mod) {};
	static constexpr std::uint32_t get_mod() { return mod; }
	std::uint32_t get() const { return n; }
	bool operator==(const modint& m) const { return n == m.n; }
	bool operator!=(const modint& m) const { return n != m.n; }
	modint& operator+=(const modint& m) { n += m.n; n = (n < mod ? n : n - mod); return *this; }
	modint& operator-=(const modint& m) { n += mod - m.n; n = (n < mod ? n : n - mod); return *this; }
	modint& operator*=(const modint& m) { n = std::uint64_t(n) * m.n % mod; return *this; }
	modint operator+(const modint& m) const { return modint(*this) += m; }
	modint operator-(const modint& m) const { return modint(*this) -= m; }
	modint operator*(const modint& m) const { return modint(*this) *= m; }
	modint inv() const { return (*this).pow(mod - 2); }
	modint pow(std::uint64_t b) const {
		modint ans = 1, m = modint(*this);
		while (b) {
			if (b & 1) ans *= m;
			m *= m;
			b >>= 1;
		}
		return ans;
	}
};

#endif // CLASS_MODINT

#include <vector>
#include <iostream>
using namespace std;
using modulo = modint<998244353>;
vector<modulo> recur(int pos, vector<int> sub, vector<vector<int> > &A) {
	if (sub.size() == 0) return { modulo(2).pow(pos + 1), modulo(0), modulo(0), modulo(0) };
	if (pos == -1) return { modulo(2).pow(sub.size() - 1), modulo(0), modulo(2).pow(sub.size() - 1), modulo(0) };
	vector<int> sub0, sub1;
	for (int i : sub) {
		if (A[pos][i] == 0) sub0.push_back(i);
		else sub1.push_back(i);
	}
	vector<modulo> res1 = recur(pos - 1, sub0, A);
	vector<modulo> res2 = recur(pos - 1, sub1, A);
	vector<modulo> mult(4);
	for (int i = 0; i < 4; ++i) {
		for (int j = 0; j < 4; ++j) {
			for (int k = 0; k < 2; ++k) {
				mult[i ^ j] += res1[i] * (k == 0 || j < 2 ? res2[j] : res2[j ^ 1]);
			}
		}
	}
	return mult;
}
int main() {
	cin.tie(0);
	ios_base::sync_with_stdio(false);
	int H, W;
	cin >> H >> W;
	vector<vector<int> > A(H, vector<int>(W));
	for (int i = 0; i < H; ++i) {
		for (int j = 0; j < W; ++j) {
			cin >> A[i][j];
		}
	}
	vector<int> all;
	for (int i = 0; i < W; ++i) all.push_back(i);
	vector<modulo> ans = recur(H - 1, all, A);
	modulo final_ans = ans[1] + ans[3];
	final_ans *= modulo(2).pow(H * (H - 1) / 2).inv();
	cout << final_ans.get() << endl;
	return 0;
}

