const int N = 310;

const ll M = 998244353;

ll pw(ll a, ll n) {
    ll ret = 1;
    while (n) {
        if (n & 1) ret = ret * a % M;
        a = a * a % M;
        n >>= 1;
    }
    return ret;
}

ll nCr[N][N];

void init() {
    nCr[0][0] = 1;
    for (int i = 1; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            nCr[i][j] = nCr[i - 1][j];
            if (j) nCr[i][j] = (nCr[i][j] + nCr[i - 1][j - 1]) % M;
        }
    }
}

int n, m;

int gauss(vector<bitset<N>> &a) {

    int kek = 0;
    fori (i, m) {
        bool ok = false;
        for (int j = kek; j < n; ++j) {
            if (a[j][i]) {
                swap(a[kek], a[j]);
                ok = true;
                break;
            }
        }
        if (ok) {
            for (int j = 0; j < n; ++j) if (j != kek) {
                if (a[j][i]) {
                    a[j] ^= a[kek];
                }
            }
            kek++;
        }
    }
    return kek;
}

void smain() {

    init();


    cin >> n >> m;
    vector<bitset<N>> a(n);
    fori (i, n) {
        fori (j, m) {
            bool cur;
            cin >> cur;
            if (cur) a[i].set(j);
        }
    }

    int r = gauss(a);
    ll ans = 0;
    for (int i = 0; i <= r; i += 2) {
        ll cur = nCr[r][i] * pw(3, r - i) % M;
        ans = (ans + cur) % M;
    }
    ans = ans * pw(2, n + m - 2 * r) % M;
    ans = (pw(2, n + m) - ans   + M) % M;
    cout << ans;
}

