int Gauss(Mat& A) {
    int rank = 0;

    REP(j, M) {
        int pivot = -1;
        FOR(i, rank, N - 1) {
            if (A[i][j] == 1) {
                pivot = i;
                break;
            }
        }
        if (pivot == -1) continue;
        swap(A[rank], A[pivot]);
        REP(i, N) {
            if (i == rank) continue;
            if (A[i][j] == 0) continue;
            REP(k, M) {
                A[i][k] ^= A[rank][k];
            }
        }
        ++rank;
    }
    return rank;
}

int binpow(int x, int p) {
    if (p == 0) return 1;

    if (p % 2 == 0)
        return binpow((x * x) % mod, p / 2);
    else
        return (x * binpow(x, p - 1)) % mod;
}

