	}

	int res = 0;
	rep(i, N)rep(j, M)res += A[i][j];

	return res;
}

signed main() {
	setkj(305);
	int n, m; cin >> n >> m;
	mat A(n);
	rep(i, n) {
		A[i].resize(m);
		rep(j, m)cin >> A[i][j];
	}
	int r = GJ(A);

	int ans = modpow(2, n - r)*modpow(2, m - r) % mod;
	int d = 0;
	for (int i = 1; i <= r; i += 2) {
		d += modpow(3, r - i)*comb(r, i) % mod;
		d %= mod;
	}
	cout << d*ans%mod << endl;
}

