#define MOD   998244353LL

bool swap_row(ll a[303][303], ll rows, ll cols, ll pos_row, ll pos_col)
{
    ll dpos_row = pos_row;
    while (!a[dpos_row][pos_col] && dpos_row < rows) dpos_row++;
    if (dpos_row == rows) return false;
    if (dpos_row == pos_row) return true;

    rep (i, cols) {
        ll tmp = a[pos_row][i];
        a[pos_row][i] = a[dpos_row][i];
        a[dpos_row][i] = tmp;
    }

    return true;
}

ll calc_rank(ll a[303][303], ll rows, ll cols)
{
    ll pos_row = 0LL;

    rep (pos_col, cols) {
        bool found = swap_row(a, rows, cols, pos_row, pos_col);
        if (!found) continue;

        srep (row, pos_row+1LL, rows) {
            if (!a[row][pos_col]) continue;
            rep (col, cols) {
                a[row][col] -= a[pos_row][col];
                a[row][col] &= 1LL;
            }
        }

        pos_row++;
        if (pos_row >= rows) break;

        debug_printf("---- pos_row=%lld\n", pos_row);
        rep (i, rows) {
            rep (j, cols) debug_printf("%lld ", a[i][j]);
            debug_printf("\n");
        }

    }

    debug_printf("---- gauss a\n");
    rep (i, rows) {
        rep (j, cols) debug_printf("%lld ", a[i][j]);
        debug_printf("\n");
    }

