    int pivot = r;
    for (int j = r+1; j < n; ++j)
      if (A[j][i] > A[pivot][i]) pivot = j;
    swap(A[pivot], A[r]);
    if (A[r][i] == 0) continue;
    for (int k = m-1; k >= i; --k)
      A[r][k] = A[r][k];
    for(int j = r+1; j < n; ++j)
      for(int k = m-1; k >= i; --k)
        A[j][k] = (A[r][k] & A[j][i]) ^ A[j][k];
    ++r;
  }
  return r;
}

