int rank_matrix(matrix<int> a) {
    const int R = a.row_size(), C = a.col_size();
    int r = 0;
    for(int i = 0; i < C && r < R; ++i) {
        int pivot = r;
        for(int j = r + 1; j < R; ++j) {
            if(std::abs(a[j][i]) > std::abs(a[pivot][i])) {
                pivot = j;
            }
        }
        std::swap(a[pivot], a[r]);
        if(a[r][i] == 0) continue;
        //for(int k = C - 1; k >= i; --k) {
        //    a[r][k] = a[r][k] / a[r][i];
        //}
        for(int j = r + 1; j < R; ++j) {
            for(int k = C - 1; k >= i; --k) {
                (a[j][k] += a[r][k] * a[j][i]) %= 2;
            }
        }
        ++r;
    }
    return r;
}

