void solve(long long N, long long M, std::vector<std::vector<long long>> a){
    sort(a.begin(),a.end(),greater<vll>());
    ll index=0;

    vector<bitset<300>> A;
    for(long long i = 0; i < a.size(); i++){
        A.push_back(bitset<300>(0));
        for(long long k = 0; k < a[i].size(); k++){
            A[i][k]=a[i][k]==1;
        }
    }
    ll rank=0;
    for(long long i = 0; i < a.size(); i++){
        DEBUG(i);
        for(; index < a[i].size(); index++){
            DEBUG(index);
            for(long long j = i; j < a.size(); j++){
                if(A[j][index]==1){
                    swap(A[j],A[i]);
                    goto OUT;
                }
            }
        }
        OUT:
        if(index==a[i].size()){
            break;
        }
        rank++;
        for(long long j = i+1; j < a.size(); j++){
            if(A[j][index]==1){
                A[j]^=A[i];
            }
        }
    }
    DEBUG(rank);
    cout<<mpow(M+N-1)-mpow(M+N-1-rank)<<endl;
}

