int main(){
    int N, M;
    while(cin >> N >> M){
        vector<vector<int>> A(N, vector<int>(M, 0));
        for(auto& v : A){
            for(auto& t : v) cin >> t;
        }
        int c = 0;
        int r = 0;
        for(int i=0;i<min(N, M);i++){
            int pivot = i;
            while(c < M){
                pivot = i;
                for(int j=i+1;j<N;j++){
                    if(A[pivot][c] < A[j][c]){
                        pivot = j;
                    }
                }
                if(A[pivot][c] == 0){ c++; continue; }
                break;
            }
            if(c == M) break;
            r++;
            if(pivot != i){
                for(int j=0;j<M;j++) swap(A[i][j], A[pivot][j]);
            }
            for(int j=i+1;j<N;j++){
                if(A[j][c] != A[i][c]) continue;
                for(int k=c;k<M;k++){
                    A[j][k] = (A[j][k] + A[i][k])%2;
                }
            }
        }
        long long powM = 1, powN = 1;
        for(int i=0;i<M-1;i++) powM = (2*powM) % MOD;
        for(int i=0;i<N;i++) powN = (2*powN) % MOD;
        powN = (powN + MOD - 1) % MOD;
        long long sub = 1;
        for(int i=N-1;i>=r;i--){
            powN = (powN + MOD - sub) % MOD;
            sub = (2*sub) % MOD;
        }
        cout << (powN*powM) % MOD << endl;
    }
}

