					B[j][k] -= B[res][k] * B[j][i];
				}
			}
			res++;
		}
		return res;
	}
	T det() {
		if (r != c) {
			cout << "()" << endl;
			exit(1);
		}
		T ans = 1;
		mat B(r, r);
		for (int i = 0; i < r; i++) {
			for (int j = 0; j < r; j++) {
				B[i][j] = (*this)[i][j];
			}
		}
		for (int i = 0; i < r; i++) {
			for (int j = i + 1; j < r; j++) {
				// i,jgcd!
				for (; B[j][i] != 0; ans = -ans) {
					// swap(-1)
					T tm = B[i][i] / B[j][i];
					for (int k = i; k < r; k++) {
						T t = B[i][k] - tm * B[j][k];
						B[i][k] = B[j][k];
						B[j][k] = t;
					}
				}
			}
			ans *= B[i][i];
		}
		return ans;
	}
	mat inverse() {
		if (r != c) {
			cout << "()" << endl;
			exit(1);
		}
		mat B(r, 2 * r);
		for (int i = 0; i < r; i++) {
			for (int j = 0; j < r; j++) {
				B[i][j] = (*this)[i][j];
			}
		}
		for (int i = 0; i < r; i++) {
			B[i][r + i] = 1;
		}
		for (int i = 0; i < r; i++) {
			int pivot = i;
			for (int j = i; j < r; j++) {
				if (abs(B[j][i]) > abs(B[pivot][i])) {
					pivot = j;
				}
			}
			if (abs(B[pivot][i]) < EPS) {
				cout << "or" << endl;
				exit(1);
			}
			swap(B[i], B[pivot]);
			for (int j = i + 1; j <= 2 * r; j++) {
				B[i][j] /= B[i][i];
			}
			for (int j = 0; j < r; j++) {
				if (i != j) {
					for (int k = i + 1; k <= 2 * r; k++) {
						B[j][k] -= B[j][i] * B[i][k];
					}
				}
			}
		}
		mat res(r, r);
		for (int i = 0; i < r; i++) {
			for (int j = 0; j < r; j++) {
				res[i][j] = B[i][r + j];
			}
		}
		return res;
	}
	void print() {
		for (int i = 0; i < r; i++) {
			for (int j = 0; j < (c - 1); j++) {
				cout << (*this)[i][j] << ",";
			}
			cout << (*this)[i][c - 1] << endl;
		}
	}
};
