      if(p[j][i]){
        for(int k = 1; i <= M; k++) p[j][k] ^= p[f][k];
      }
    }
  }

  int a = _pow(N), b = _pow(N - f), c = _pow(M - 1);
  int ans = 1LL * (a - b < 0 ? a - b + MOD : a - b) * c % MOD;

  printf("%d\n", ans);
  return 0;
}

