ll powMod(ll n, ll p, ll mod) {
	ll res = 1;
	while (p) {
		if (p & 1) (res *= n) %= mod;
		(n *= n) %= mod;
		p >>= 1;
	}
	return res;
}
ll invMod(ll n, ll mod) {
	return powMod(n, MOD - 2, MOD);
}

const signed FACT_MAX_N = 1000006;
signed fact[FACT_MAX_N];
signed factInv[FACT_MAX_N];
struct INIT_FACT {
	INIT_FACT() {
		fact[0] = 1;
		for (int i = 1; i < FACT_MAX_N; ++i) fact[i] = (long long)i * fact[i - 1] % MOD;
		factInv[FACT_MAX_N - 1] = powMod(fact[FACT_MAX_N - 1], MOD - 2, MOD);
		for (int i = FACT_MAX_N - 2; i >= 0; --i) factInv[i] = (long long)(i + 1) * factInv[i + 1] % MOD;
	}
} init_fact;

/* n,r<=10^6, query O(1)*/
ll Permutation(int n, int r) {
	if (r == 0) return 1;
	if (n < r) return 0;
	return (long long)fact[n] * factInv[n - r] % MOD;
}
ll Combination(int n, int r) {
	if (r == 0) return 1;
	if (n < r) return 0;
	return (long long)fact[n] * factInv[n - r] % MOD * factInv[r] % MOD;
}
ll CombinationWithRepetition(ll n, ll r) {
	return Combination(n + r - 1, r);
}


void norm(VV<int>& a) {
	int n = a.size(), m = a[0].size();
	REP(j, m) {
		int pivot = -1;
		REP(i, n) {
			if (a[i][j]) {
				pivot = i;
				break;
			}
		}
		if (pivot == -1) continue;

		FOR(i, pivot + 1, n) {
			if (a[i][j]) {
				REP(k, m) {
					a[i][k] ^= a[pivot][k];
				}
			}
		}
	}
}

signed main() {
	INIT;

	VAR(int, n, m);
	MAT(int, a, n, m);

	REP(_, 2) {
		norm(a);
		VV<int> b(m, V<int>(n));
		REP(i, n) REP(j, m) {
			b[j][i] = a[i][j];
		}
		std::swap(n, m);
		std::swap(a, b);
	}

	int one = 0;
	int zero = 0;
	REP(i, n) {
		bool f = false;
		REP(j, m) if (a[i][j]) f = true;
		if (f) ++one;
		else ++zero;
	}
	REP(j, m) {
		bool f = false;
		REP(i, n) if (a[i][j]) f = true;
		if (f) /*++one*/;
		else ++zero;
	}

	int ans = 0;
	for (int i = 1; i <= one; i += 2) {
		int sum = Combination(one, i);
		(sum *= powMod(3, one - i, MOD)) %= MOD;
		(sum *= powMod(2, zero, MOD)) %= MOD;
		(ans += sum) %= MOD;
	}
	OUT(ans)BR;
	return 0;
}

