#define rep(i, n) for(UL i = 0; i < (n); i++)

struct Problem {

    UL G[300][300];

    static const ULL M = 998244353;

    void Solve() {
        UL H, W; scanf("%d%d", &H, &W);
        rep(y, H) rep(x, W) {
            char c; scanf(" %c", &c);
            G[x][y] = c - '0';
        }
        UL Ker = 0;
        UL K = 0;
        rep(y, H) {
            UL Y = 0;
            while (true) {
                if (K == W) break;
                for (Y = y; Y < H; Y++) if (G[K][Y]) break;
                if (Y < H) break;
                K++;
            }
            if (K == W) { Ker = H - y; break; }
            rep(x, W) swap(G[x][y], G[x][Y]);
            for (UL yy = y + 1; yy < H; yy++) {
                if (G[K][yy]) {
                    for (UL x = K; x < W; x++)
                        G[x][yy] = G[x][yy] ^ G[x][y];
                }
            }
        }
        ULL ans = 1; rep(i, H + W - 1) ans = ans * 2 % M;
        ULL sub = 1; rep(i, Ker + W - 1) sub = sub * 2 % M;
        ans = (ans + M - sub) % M;
        cout << ans << endl;
    }
};
int main() {
    unique_ptr<Problem> p(new Problem());
    //cout << fixed << setprecision(10);
    p->Solve();
