const long long mod = 998244353;
int a[301][301];
int b[301][301];
int c[301];

long long power(long long a,long long b){
    long long res = 1;
    while(b){
        if(b&1) res = res*a%mod;
        a = a*a%mod;
        b /= 2;
    }
    return res;
}

void swapRow(int i,int j,int m){
    int t = c[i];
    c[i] = c[j];
    c[j] = t;
    for(int k = 1; k <= m; k++){
        int t = b[i][k];
        b[i][k] = b[j][k];
        b[j][k] = t;
    }
}

long long gauss(int n,int m){
    long long ans = 0;
    for(int i = 1; i <= n; i++){
        int find = -1, findBit = -1;
        for(int j = i; j <= n; j++){
            int bit = -1;
            for(int k = m; k >= 1; k--) if(b[j][k]) bit = k;
            if(bit!=-1 && (find==-1 || bit<findBit)){
                find = j;
                findBit = bit;
            }
        }
        //printf("i = %d, find = %d, findBit = %d\n",i,find,findBit);
        if(find==-1){
            //printf("i = %d\n",i);
            int ok = 1;
            for(int j = i; j <= n; j++) if(c[j]==1) ok = 0;
            if(ok) ans = power(2,m-(i-1)); 
            else ans = 0;
            break; 
        }
        else{
            swapRow(i,find,m);
            for(int j = 1; j <= n; j++){
                if(j!=i && b[j][findBit]!=0){
                    for(int k = 1; k <= m; k++) b[j][k] ^= b[i][k];
                    c[j] ^= c[i]; 
                }
            }
        }
        if(i==n) ans = power(2,m-n);
    }
    return ans;
}

