vector<BS> Gauss_Jordan(const vector<BS> &v){
    int n=v.size();
    vector<BS> ret=v;
    int d=0;
    for(int k=0; k<MAX; k++){
        int p=-1;
        for(int i=d; i<n; i++){
            if(ret[i][k]){
                p=i;
                break;
            }
        }
        if(p==-1) continue;
        swap(ret[d], ret[p]);
        for(int i=0; i<n; i++){
            if(i==d) continue;
            if(ret[i][k]) ret[i]^=ret[d];
        }
        d++;
    }
    return ret;
}
