template <typename T>
T mypow(T a, T b) {
  if (b == 0) return 1;
  T tmp = mypow(a, b / 2);
  if (b % 2)
    return (((tmp * tmp) % mod) * a) % mod;
  else
    return (tmp * tmp) % mod;
}

int main() {
  ll n, m;
  cin >> n >> m;
  matrix a(n, vector<int>(m));
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < m; j++) {
      cin >> a[i][j];
    }
  }

  int rank = 0;
  for (int j = 0; j < m; j++) {
    int pivot = -1;
    for (int i = rank; i < n; i++) {
      if (a[i][j]) {
        pivot = i;
        break;
      }
    }
    if (pivot >= 0) {
      if (pivot != rank)
        for (int k = 0; k < m; k++) swap(a[pivot][k], a[rank][k]);
      for (int i = rank + 1; i < n; i++) {
        if (a[i][j])
          for (int k = 0; k < m; k++) a[i][k] ^= a[rank][k];
      }
      rank++;
    }
  }

  ll ret = mypow(2LL, n + m - 1) + mod - mypow(2LL, n + m - rank - 1);
  cout << ret % mod << endl;
}

