    int rank = 0;
    for(int j=0; j<M; j++){
        int pivot = -1;
        for(int i=rank; i<N; i++){
            if(A[i][j]){
                pivot = i;
                break;
            }
        }
        if(pivot >= 0){
            if(pivot != rank) for(int k=0; k<M; k++) swap(A[pivot][k], A[rank][k]);
            for(int i=rank+1; i<N; i++){
                if(A[i][j]) for(int k=i; k<M; k++) A[j][k] ^ A[rank][k];
            }
            rank++;
        }
    }

    int64_t ans = (MOD + power_mod(2, N+M-1) - power_mod(2, N+M-rank-1)) % MOD;
    cout << ans << endl;
    return 0;
}

