		if (b % 2 == 1) {
			ans *= a;
			ans %= MOD;
		}
		a *= a;
		a %= MOD;
		b /= 2;
	}
	return ans;
}
int main() {
	int n, m;
	cin >> n >> m;
	REP(i, n) {
		REP(q, m) {
			cin >> grid[i][q];
		}
	}
	long long rank = 0;
	for (int i = 0; i < n; ++i) {
		int itr = -1;
		for (int q = 0; q < m; ++q) {
			if (bad[q] == 1) continue;
			if (grid[i][q] == 1) {
				itr = q;
				break;
			}
		}
		if (itr == -1) continue;
		for (int q = 0; q < m; ++q) {
			if (grid[i][q] == 1 && q != itr) {
				for (int j = 0; j < n; ++j) {
					grid[j][q] += grid[j][itr];
					grid[j][q] %= 2;
				}
			}
		}
		bad[itr] = 1;
		rank++;
	}
	cout << (powing(2, n + m - 1) - powing(2, n + m - 1 - rank) + MOD) % MOD << endl;
}

