using Mod2 = ModInt<2>;
using Type = ModInt<998244353>;
using Vector = vector<Mod2>;
using Matrix = vector<Vector>;

int Rank(Matrix &A) {
    const int N = A.size(), M = A[0].size();
    int rank = 0;
    vector<bool> used(N, false);

    for (int c = 0; c < M; ++c) {
        int pivot = 0;
        while (pivot < N && (used[pivot] || A[pivot][c] == 0)) ++pivot;
        if (pivot == N) continue;

        ++rank;
        Mod2 v = A[pivot][c];
        for (int j = 0; j < N; ++j) A[pivot][j] /= v;
        for (int r = 0; r < N; ++r) {
            if (r == pivot) continue;
            for (int j = c + 1; j < M; ++j)
                A[r][j] -= A[r][c] * A[pivot][j];
        }
    }

    return rank;
}

