// >>> gauss jordan, det
template <class T>
int gauss_jordan(vector<vector<T>> &a) { // return rank
    if (a.empty()) return 0;
    const int h = a.size(), w = a[0].size();
    int i = 0;
    for (int j = 0; i < h && j < w; j++) {
        int k = i;
        while (k < h && a[k][j] == 0) k++; //
        if (k == h) continue;
        swap(a[i],a[k]);
        rep (x,h) if (x != i && a[x][j] != 0) {
            auto const d = a[x][j]/a[i][j];
            rep (y,w) a[x][y] -= d*a[i][y];
        }
        i++;
    }
    return i;
}
template <class T>
T det(vector<vector<T>> a) {
    const int n = a.size();
    rep (i,n) assert((int)a[i].size() == n);
    gauss_jordan(a);
    T ans = 1;
    rep (i,n) ans *= a[i][i];
    return ans;
}
