ll gcd(ll a, ll b) {
  if (a < 0) { a *= (-1); }
  if (b < 0) { b *= (-1); }
  if (b == 0) {
    return a;
  }
  return gcd(b, a % b);
}

ll lcm(ll a, ll b) {
  if (a < 0) { a *= (-1); }
  if (b < 0) { b *= (-1); }
  return a / gcd(a, b) * b;
}

vvll gaussian_elimination_mod2(vvll a) {
  int n = a.size();
  int m = a[0].size();
  int k_add = 0;
  rep (k, n) {
    while (true) {
      if (k + k_add >= m) {
        return a;
      }
      if (a[k][k + k_add] == 0) {
        FOR (i, k+1, n) {
          if (a[i][k + k_add] != 0) {
            swap(a[k], a[i]);
            break;
          }
        }
      }
      if (a[k][k + k_add] != 0) {
        break;
