      if (a[i][k + k_add] == 0) { continue; }
      FOR (j, k + k_add, m) {
        a[i][j] = a[i][j] - a[k][j];
        if (a[i][j] == -1) { a[i][j] *= (-1); }
      }
    }
  }
  return a;
}

int calc_rank(vvll a) {
  int n = a.size();
  int m = a[0].size();
  int ret = 0;
  rep (i, n) {
    rep (j, m) {
      if (a[i][j] != 0) {
        ret += 1;
        break;
      }
    }
  }
  return ret;
}

ll po(ll k, ll x) {
  if (x == 0) {
    return 1;
  }
  if (x == 1) {
    return k % mod;
  }
  ll y = po(k, x/2);
  y = y * y % mod;
  if (x % 2 == 1) {
    y = y * k % mod;
  }
  return y;
}

int main() {
  int n, m;
  scan(n, m);
  vvll a(n, vll(m));
  scan(a);

  auto b = gaussian_elimination_mod2(a);
  int rank_a = calc_rank(b);

  ll ans = po(2, n) - po(2, n - rank_a) + mod;
  ans %= mod;
  ans = (ans * po(2, m-1)) % mod;
  print(ans);

  return 0;
}

