int Gauss(vector<vector<int>> &mat) {

    int rank = 0;
    int n = (int)mat.size();

    for(int i = 0; i < n; i++) {
        bool ok = false;
        for(int j = i; j < n; j++) {
            if(mat[j][i]) {
                swap(mat[j], mat[rank]);
                ok = true;
                break;
            }
        }

        if(!ok) continue;


        for(int j = i+1; j < n; j++) if(mat[j][i]) {

            for(int k = i; k < n; k++) {

                mat[j][k] ^= mat[i][k];
            }
        }

        rank++;
    }

    return rank;
}



