int const N = 300, md = 998244353;
int n, m, p2[N + 1];
vector<bitset<N> > g;

void ad(bitset<N> v){
	int j = 0;
	f(i, 0, g.size()){
		if (v.none())return;
		while (!v[j] && !g[i][j])++j;
		if (g[i][j] && v[j])v ^= g[i];
		else if (v[j]){
			g.insert(g.begin() + i, v);
			return;
		}
	}
	if (v.any())g.push_back(v);
}

int main(){
	p2[0] = 1;
	f(i, 1, N + 1)p2[i] = (ll)p2[i - 1] * 2 % md;
	scanf("%d%d", &n, &m);
	f(i, 0, n){
		bitset<N> v;
		f(j, 0, m){
			int t;
			scanf("%d", &t);
			if (t)v[j] = true;
		}
		ad(v);
	}
	int an = p2[n] - p2[n - g.size()] + md;
	an = (ll)an * p2[m - 1] % md;
	printf("%d\n", an);
}

