void dij(int st, long long dis[], int cnt[]) {
	for (int i = 1; i <= n; i++) {
		dis[i] = inf;
	}
	dis[st] = 0;
	cnt[st] = 1;
	s.insert({0, st});
	while (s.size()) {
		int v = (*s.begin()).second;
		s.erase(s.begin());
	//	cout << "74 " << v << endl;
		for (auto p : adj[v]) {
			int u = p.first, w = p.second;
		//	cout << "73 " << v << " " << u << endl;
			if (dis[v] + w < dis[u]) {

				s.erase({dis[u], u});
				dis[u] = dis[v] + w;
				cnt[u] = 0;
				s.insert({dis[u], u});
			}
			if (dis[v] + w == dis[u]) {
				cnt[u] += cnt[v];
				cnt[u] %= mod;
			}
		}
	}
}

