			long long x = 1ll * dp1[i] * dp1[i];
			x %= mod;
			x *= x;
			x %= mod;
			ans -= x - mod;
			ans %= mod;
		}
		for (auto p : adj[i]) {
			int u = p.first, w = p.second;
			if (dis1[i] < d / 2 && d / 2 < dis1[u] && 0ll + dis1[i] + w + dis2[u] == d) {
