  ll p = 0;
  REP(j, m){
    int piv = p;
    FOR(i, p, n){
      if(A[piv][j] < A[i][j]){
        piv = i;
      }
    }
    swap(A[p], A[piv]);
    if(A[p][j] == 0){
      continue;
    }
