const BS one=BS().set(0);
const BS zero=BS();
const LL M=998244353;

struct LB {
	int sz;
	BS b[B];

	void build(BS *a,int n) {
		sz=0;
		for(int i=0;i<B;i++) {
			b[i].reset();
		}
		for(int i=0;i<n;i++) {
			BS x=a[i];
			for(int j=B-1;j>=0;j--) {
				if(((x>>j)&one)==one) {
					if(b[j]!=zero) x^=b[j];
					else {
						b[j]=x;
						sz++;
						for(int k=j-1;k>=0;k--) {
							if(b[k]!=zero && ((b[j]>>k)&one)==one) b[j]^=b[k];
						}
						for(int k=j+1;k<B;k++) {
							if(((b[k]>>j)&one)==one) b[k]^=b[j];
						}
						break;
					}
				}
			}
		}
	}
}lb;

int n,m,b;
BS a[N];
LL dp[N],P[N],inv_P[N],pw[N];

LL exgcd(LL a,LL b,LL &x,LL &y) {
	LL d=a;
	if(b!=0) {
		d=exgcd(b,a%b,y,x);
		y-=(a/b)*x;
	}
	else {
		x=1;y=0;
	}
	return d;
}

LL mod_inv(LL a,LL m) {
	LL x,y;
	exgcd(a,m,x,y);
	return (m+x%m)%m;
}

void init() {
	P[0]=1;
	inv_P[0]=1;
	pw[0]=1;
	for(int i=1;i<N;i++) {
		P[i]=P[i-1]*i%M;
		inv_P[i]=mod_inv(P[i],M);
		pw[i]=pw[i-1]*2%M;
	}
}

LL C(int n,int m) {
	return P[n]*inv_P[m]%M*inv_P[n-m]%M;
}

int main() {
