	lb.build(a,n);

	dp[0]=1;
	for(int i=0;i<B;i++) {
		int l=lb.b[i].count();
		if(l==0) continue;
		for(int j=m;j>=0;j--) {
			if(j+l<=m) dp[j+l]+=dp[j],dp[j+l]%=M;
		}
	}
	LL ans=0;
	for(int i=1;i<=m;i++) {
		for(int j=1;j<=i;j+=2) {
			ans+=C(i,j)*dp[i]%M*pw[m-i]%M;
			ans%=M;
		}
	}
	ans*=pw[n-lb.sz],ans%=M;
	printf("%lld\n",ans);
}

