        }

        a[row][col] /= a[row][col];

        for (int i=0; i<n; ++i)
            if (i != row) {
                ll c = a[i][col] / a[row][col];

                assert(a[i][col] % a[row][col] == 0LL);
                for (int j=col; j<m; ++j)
                    a[i][j] -= a[row][j] * c;
            }
        ++row;
    }

    int rank = n;
    // cout << "m = " << m << endl;
    for(int i = 0 ; i < n ; i++){
        bool has_one = false;

        for(int j = 0 ; j < m ; j++){
            if(a[i][j]){
                has_one = true;
                break;
            }
        }

        if(!has_one){
            // cout << "Linha " << i << " nao tem! " << endl;
            rank--;
        }
    }

    return rank;
}

