inline int fpw(int x, int k) {
	int res = 1;
	for (; k; k >>= 1, x = (ll) x * x % P)
		if (k & 1) res = (ll) res * x % P;
	return res;
}

inline int read() {
	int x; bool f; char c;
	for (f = 0; (c = getchar()) < '0' || c > '9'; f = c == '-');
	for (x = c ^ '0'; (c = getchar()) >= '0' && c <= '9'; x = (x << 1) + (x << 3) + (c ^ '0'));
	return f ? -x : x;
}

