        if (r == N) continue;
        rank++;
        used[r] = true;
        for (int i=0;i<N;i++){
            if (i == r) continue;
            if (mat[i][c] == 1) {
                for (int j=0;j<M;j++) {
                    mat[i][j] ^= mat[r][j];
                }
            }
        }
    }

    mi ans = pow(mi(2), M+N-1) - pow(mi(2), M+N-1-rank);
    cout << ans.val << endl;

    // cout << rank << endl;


}

