vector< bitset<N> > basis;

int lstBit(bitset<N> x) {
    int ret = -1;
    for (int i = 0; i < N; ++i) if (x[i]) ret = i;
    return ret;
}

void updBasis(bitset<N> val) {
    for (int i = 0; i < (int)basis.size(); ++i) {
        if (!val.count() ) return ;
        auto _ = basis[i];
        int lst_ = lstBit(_),
            lstVal = lstBit(val);
        if (lst_ == lstVal) val ^= _;
        else if (lst_ < lstVal) {
            basis.insert(basis.begin() + i, val);
            return ;
        }
    }
    if (val.count() ) basis.push_back(val);
}

int add(int _a, int _b) {
    _a += _b;
    if (_a >= mod) _a -= mod;
    return _a;
}
int sub(int _a, int _b) { return add(_a, mod - _b); }
int mul(int _a, int _b) { return (int)( (ll)_a * _b % mod); }

int main() {
    ios_base::sync_with_stdio(0); cin.tie(0); cout.tie(0);

    cin >> n >> m;
    for (int i = 1; i <= n; ++i) {
        bitset<N> val;
        for (int j = 1; j <= m; ++j) {
            int a; cin >> a;
            val[j] = a;
        }
        updBasis(val);
    }

    pow2[0] = 1; for (int i = 1; i < (N << 1); ++i) pow2[i] = (int)(2 * pow2[i - 1] % mod);

    cout << sub(pow2[n + m - 1], pow2[n + m - 1 - (int)basis.size()]);

    return 0;
}

