ll modpower(ll x, ll y) {
    if (y == 0) {
        return 1;
    } else if (y == 1) {
        return x % mod;
    } else if (y % 2 == 0) {
        ll p = modpower(x, y / 2);
        return p * p % mod;
    } else {
        ll p = modpower (x, y / 2);
        return (p * p) % mod * (x % mod) % mod;
    }
}

ll matrixrank(vector< vector<double> > mat) {
    ll h = mat.size();
    ll w = mat[0].size();
    ll now = 0;
    for (ll i = 0; i < w; i++) {
        ll pivot = now;
        for (ll j = now + 1; j < h; j++) {
            if (abs(mat[pivot][i]) > eps) {
                break;
            }
            pivot = j;
        }
        if (abs(mat[pivot][i]) < eps) {
            continue;
        }
        swap(mat[now], mat[pivot]);
        for (ll j = i + 1; j < w; j++) {
            mat[now][j] /= mat[now][i];
        }
        for (ll j = now + 1; j < h; j++) {
            for (ll k = i + 1; k < w; k++) {
                mat[j][k] -= mat[j][i] * mat[now][k];
            }
        }
        now++;
        if (now == h) {
            break;
        }
    }
    return now;
}

