        REP(j, 0, M) {
            mat[i][j] = a[i][j];
        }
    }
    ll rank = matrixrank(mat);
    // cout << rank << endl;
    ll ans = modminus(modpower(2, N + M - 1), modpower(2, N + M - rank - 1));
    cout << ans << endl;
}

