int compute_rank(vector< vector< int > > A) {
    int n = A.size();
    int m = A[0].size();
    // cout << n << " " << m << endl;
    int rank = n;
    vector<bool> row_selected(n, false);
    for (int i = 0; i < m; ++i) {
        int j;
        for (j = 0; j < n; ++j) {
            if (!row_selected[j] && A[j][i])
                break;
        }

        if (j == n) {
            // --rank;
        } else {
            row_selected[j] = true;
            for (int p = i + 1; p < m; ++p)
                A[j][p] /= A[j][i];
            for (int k = 0; k < n; ++k) {
                if (k != j && A[k][i]) {
                    for (int p = i + 1; p < m; ++p)
                        A[k][p] -= A[j][p] * A[k][i];
                }
            }
        }
    }

    for(int i = 0 ; i < n ; i++){
        if(!row_selected[i]){
            --rank;
        }
    }

    return rank;
}

