  ll t=0,y=0;
  bool f;
  for(;;){
    f=0;
    if(M[t][y]==0){
      f=1;
      for(ll i=t+1;i<n;i++){
        if(M[i][y]!=0){
          f=0;
          swap(M[t],M[i]);
