typedef double number;
const number eps = 1e-8;
typedef vector<number> array1;
typedef vector<array1> matrix;

// O( n )
matrix identity1(int n,int m) {
	matrix A(n, array1(m));
	return A;
}
// O( n^3 ).
int rank1(matrix A) {
	const int n = A.size(), m = A[0].size();
	int r = 0;
	for (int i = 0; r < n && i < m; ++i) {
		int pivot = r;
		for (int j = r + 1; j < n; ++j)
			if (abs(A[j][i]) > abs(A[pivot][i])) pivot = j;
		swap(A[pivot], A[r]);
		if (abs(A[r][i]) < eps) continue;
		for (int k = m - 1; k >= i; --k)
			A[r][k] /= A[r][i];
		for (int j = r + 1; j < n; ++j)
			for (int k = i; k < m; ++k)
				A[j][k] -= A[r][k] * A[j][i];
		++r;
	}
	return r;
}

ll mpow(int n, int k, int p) {
	if (k == 0)return 1;
	if (k % 2)return n * mpow(n, k - 1, p) % p;
	else {
		int sub = mpow(n, k / 2, p);
		return sub * sub% p;
	}
}

