ll matrixrank(vector< vector<double> > mat) {
    ll h = mat.size();
    ll w = mat[0].size();
    ll now = 0;
    for (ll i = 0; i < w; i++) {
        ll pivot = now;
        for (ll j = now + 1; j < h; j++) {
            if (abs(mat[pivot][i]) > eps) {
                break;
            }
            pivot = j;
        }
        if (abs(mat[pivot][i]) < eps) {
            continue;
        }
        swap(mat[now], mat[pivot]);
        for (ll j = i + 1; j < w; j++) {
            mat[now][j] /= mat[now][i];
        }
        for (ll j = now + 1; j < h; j++) {
            for (ll k = i + 1; k < w; k++) {
                mat[j][k] -= mat[j][i] * mat[now][k];
            }
        }
        now++;
        if (now == h) {
            break;
        }
    }
    return now;
}

