void GaussianElimination(int mat[305][305], int w, int h)
{
	int r = 0;
	for(int i = 0; i < w && r < h; i++){
		if(mat[i][r] == 0){
			int max_val = 0, max_j;
			for(int j = r+1; j < h; j++){
				if(mat[j][i] > max_val){
					max_val = mat[j][i];
					max_j = j;
				}
			}
			if(max_val == 0) goto end;
			swap(r, max_j);
		}

		for(int j = 0; j < h; j++){
			if(j == r) continue;
			if(mat[j][i] == 0) continue;
			for(int k = 0; k <= w; k++){
				mat[j][k] ^= mat[r][k];
			}
		}
		r++;
		end:;
	}
}

int main(void)
{
	cin >> h >> w;
	for(int i = 0; i < h; i++){
		for(int j = 0; j < w; j++){
			cin >> a[i][j];
		}
	}

	GaussianElimination(a, w, h);

	llint rank = 0;
	for(int i = 0; i < h; i++){
		bool flag = false;
		for(int j = 0; j < w; j++) if(a[i][j]) flag = true;
		if(flag) rank++;
	}

	llint tmp = 1, tmp2 = 1;
	for(int i = 0; i < h-rank; i++) tmp *= 2, tmp %= mod;
	for(int i = 0; i < h; i++) tmp2 *= 2, tmp2 %= mod;
	llint ans = (tmp2 - tmp + mod) % mod;
	for(int i = 0; i < w-1; i++) ans *= 2, ans %= mod;

	cout << ans << endl;
}

