template <typename T>
std::size_t Rank(Matrix<T> mat)
{
    std::size_t dim = 0, pos = 0;
    for (std::size_t i = 0, piv = mat.C; i < mat.C and pos < mat.R; i++, pos++, piv = mat.C) {
        if (mat[pos][i] == 0) {
            for (std::size_t k = pos + 1; k < mat.R; k++) {
                if (mat[k][i] != 0) {
                    piv = k;
                    break;
                }
            }
            if (piv != mat.C) {
                std::swap(mat[pos], mat[piv]);
            } else {
                pos--;
                continue;
            }
        }
        for (std::size_t j = 0; j < mat.R; j++) {
            if (pos == j) { continue; }
            if (mat[j][i]) {
                const T p = mat[j][i] / mat[pos][i];
                for (std::size_t k = 0; k < mat.C; k++) { mat[j][k] -= mat[pos][k] * p; }
            }
        }
        dim++;
    }
    return dim;
}
