int Pow(int x, int n){
  if(n < 0)n += mod-1;
  int ans = 1;
  while(n > 0){
    if(n & 1)ans = (ans*x)%mod;
    x = (x*x)%mod;
    n >>= 1;
  }
  return ans;
}

int calc_rank(int h, int w){
