bs matrix[305];

ll modpow(ll b, ll e, ll m) {
  ll r = 1;
  while(e) {
    if(e%2) {
      r*=b;
      r%=m;
    }
    b*=b;
    b%=m;
    e/=2;
  }
  return r;
}

void solve() {
  int r, c;
  cin >> r >> c;
  for(int i = 0; i < r; i++) {
    for(int j = 0; j < c; j++) {
      int t;
      cin >> t;
      if(t) matrix[i].set(j);
    }
  }
  int rank = 0;
  for(int j = 0; j < c; j++) {
    bool found = false;
    for(int i = rank; i < r; i++) {
      if(matrix[i][j]) {
        found = true;
        if(i != rank) swap(matrix[i], matrix[rank]);
      }
    }
    if(found) {
      for(int i = rank+1; i < r; i++) {
        if(matrix[i][j]) matrix[i] ^= matrix[rank];
      }
      rank++;
    }
  }
  ll ret = modpow(2, r+c-1, MOD);
  ret -= modpow(2, r+c-1-rank, MOD);
  ret += MOD;
  cout << ret%MOD << "\n";
}

