int min_bit(vector<int> &a) {
    int p = 0;
    while (p < sz(a) && a[p] == 0) {
        p++;
    }
    return p;
}

vector<int> check(vector<vector<int> > &basis, vector<int> v) {
    rep(i, 0, sz(basis)) {
        int b = min_bit(basis[i]);
        if (v[b] == 1) {
            rep(j, 0, sz(v)) {
                v[j] ^= basis[i][j];
            }
        }
    }
    return v;
}

bool is_zero(vector<int> &a) {
    rep(i, 0, sz(a)) {
        if (a[i] != 0) {
            return false;
        }
    }
    return true;
}

int solve(int n, vector<vector<int> > a) {
    vector<vector<int> > basis;
    rep(i, 0, n) {
        vector<int> v = check(basis, a[i]);
        if (!is_zero(v)) {
            basis.push_back(v);
            int p = sz(basis) - 1;
            while (p - 1 >= 0) {
                if (min_bit(basis[p]) < min_bit(basis[p - 1])) {
                    basis[p].swap(basis[p - 1]);
                    p--;
                } else {
                    break;
                }
            }
        }
    }
    return sz(basis);
}

