int gauss (vector < vector< double > > a) {
    int n = (int) a.size();
    int m = (int) a[0].size();

    vector<int> where (m, -1);
    for (int col=0, row=0; col<m && row<n; ++col) {
        int sel = row;
        for (int i=row; i<n; ++i)
            if (fabs (a[i][col]) > fabs (a[sel][col]))
                sel = i;
        if (fabs(a[sel][col]) < EPS)
            continue;
        for (int i=col; i<m; ++i)
            swap (a[sel][i], a[row][i]);
        where[col] = row;

        // for(int i = col + 1 ; i < m ; i++){
        //     assert(a[row][i] % a[row][col] == 0LL);

        //     a[row][i] /= a[row][col];
        // }

        for (int i=0; i<n; ++i)
            if (i != row) {
                double c = a[i][col] / a[row][col];

                // assert(a[i][col] % a[row][col] == 0LL);
                for (int j=col; j<m; ++j)
                    a[i][j] -= a[row][j] * c;
            }
        ++row;
    }

    int rank = n;
    // cout << "m = " << m << endl;
    for(int i = 0 ; i < n ; i++){
        bool has_one = false;

        for(int j = 0 ; j < m ; j++){
            if(fabs(a[i][j]) > EPS){
                has_one = true;
                break;
            }
        }

        if(!has_one){
            // cout << "Linha " << i << " nao tem! " << endl;
            rank--;
        }
    }

    return rank;
}

