ll rnk(ll n,ll m,ll M[][310]){
  ll d=0;
  fr(i,n){
    bool f=0;
    if(M[i][d]==0){
      f=1;
      for(ll k=i+1;k<n;k++){
        if(M[k][d]!=0){
          f=0;
          swap(M[i],M[k]);
        }
      }
    }
    if(f && d<m-1){i--;d++;continue;}
    if(f) return i;
    for(ll k=i+1;k<n;k++){
      if(M[k][d]==0) continue;
      for(ll j=d;j<m;j++){
        M[k][j]=(M[k][j]+M[k][d])%2;
      }
    }
    if(i==n-1) return n;
  }
}

