template <typename T>
void calcFactional_mod( size_t size, 
	vector<T>& fac, vector<T>& inv, vector<T>& facInv, T mod )
{
	for( T i = 2; i < size; ++i )
	{
		fac[i] = fac[i - 1] * i % mod; // n! % mod
		inv[i] = mod - inv[mod % i] * ( mod / i ) % mod; //  (n)^-1 % mod
		facInv[i] = facInv[i - 1] * inv[i] % mod; // (n!)^-1 % mod
	}
}
template <typename T>
T pow_mod(T x, T n, T mod)
{
	T res = 1;
	while (n > 0)
	{
		if (n & 1)
			res = (res * x) % mod;
		x = (x * x) % mod;
		n >>= 1;
	}
	return res;
}


template< typename T >
class Matrix
{
public:
	explicit Matrix( size_t row_size, size_t col_size )
	{
		assign( row_size, col_size );
	}

	void assign( size_t row_size, size_t col_size )
	{
		mElement.assign( row_size, vector<T>() );
		REP( i, row_size ) mElement[i].assign( col_size, T(0) );
	}

	const vector<T>& operator[] ( size_t row ) const
	{ return mElement[row]; }
	vector<T>& operator[] ( size_t row )
	{ return mElement[row]; }

	const size_t rowSize() const
	{ return mElement.size(); }
	const size_t colSize() const
	{ return mElement[0].size(); }

	/*T get( size_t row, size_t col ) const
	{ return mElement[row][col]; }
	void set( size_t row, size_t col, T value )
	{ mElement[row][col] = value; }*/

	Matrix<T> operator* ( const Matrix& rhs ) const
	{
		size_t k_ = min( this->colSize(), rhs.rowSize() );
		Matrix ret( this->rowSize(), rhs.colSize() );
		REP( i, ret.rowSize() )
		REP( k, k_ )
		REP( j, ret.colSize() )
			ret[i][j] = ret[i][j] + (*this)[i][k] * rhs[k][j];
		return ret;
	}

	vector<T> operator* ( const vector<T>& rhs ) const
	{
		size_t k_ = min( this->colSize(), rhs.size() );
		vector<T> ret( this->rowSize(), T() );
		REP( i, ret.size() )
		REP( k, k_ )
			ret[i] = ret[i] + (*this)[i][k] * rhs[k];
		return ret;
	}

	// O( N^3 )
	// Ax = b 
	// T = double 
	struct GaussJordanData
	{
		int rank = 0;
		int result = 0; // -1: 0: 1:
		vector<T> solution;
	};
	GaussJordanData gaussJordan() const
	{
		vector<T> b( rowSize(), 0 );
		return gaussJordan( b );
	}
	GaussJordanData gaussJordan( const vector<T>& b ) const
	{
		GaussJordanData data;
		data.result = 1;

		// 1b
		Matrix<T> B( rowSize(), colSize() + 1 );
		REP( i, rowSize() ) REP( j, colSize() + 1 )
		{
			if( j < colSize() ) B[i][j] = (*this)[i][j];
			else B[i][j] = b[i];
		}

		for( int i = 0, j = 0; i < rowSize() && j < colSize(); ++i, ++j )
		{
			// ji
			size_t pivot = i;
			for( int k = i + 1; k < rowSize(); ++k )
				if( abs( B[k][j] )> abs( B[pivot][j] ) ) pivot = k;
			swap( B[i], B[pivot] );
			// 
			if( abs( B[i][j] ) < numeric_limits<double>::epsilon() )
			{
				--i;
				data.result = 0;
				continue;
			}
			// Rank1
			++data.rank;
			// [i][j]1i
			for( int l = j + 1; l <= colSize(); ++l )
				B[i][l] /= B[i][j];
			B[i][j] = 1;
			// kij
			REP( k, rowSize() ) if( k != i )
			{
				T coef = B[k][j];
				for( int l = j; l <= colSize(); ++l )
					B[k][l] -= coef * B[i][l];
			}
		}

		// x
		data.solution.assign( colSize(), T() );
		for( int i = 0, j = 0; j < colSize(); ++i, ++j )
		{
			if( i >= rowSize() || B[i][j] == 0 )
			{
				data.solution[j] = 0;
				--i; continue;
			}
			else data.solution[j] = *( B[i].rbegin() );
		}

		// 
		if( data.result == 0 || ( data.rank ) < b.size() )
		{
			auto m = ( *this ) * data.solution;
			REP( i, m.size() ) if( m[i] != b[i] )
			{ data.result = -1; break; }
		}
